import React from "react";
class NumArray extends React.Component {
  state = {
    todosList: [
      {
        id: 1,
        task: "task 1",
      },
      {
        id: 2,
        task: "task 2",
      },
      {
        id: 3,
        task: "task 3",
      },
      {
        id: 4,
        task: "task 4",
      },
    ],
  };

  addTodo = (addTodo) => {
    this.setState({
      todosList: [...this.state.todosList, addTodo],
    });
  };

  getTodo = () => this.state.todosList;
}

export default NumArray;
