import React from "react";
import "./index.css";
import MainContent from "./MainContent";

class AddContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        {
          task: "sdvefnvefo",
          id : "fivnffepv"
        }
      ],
      currentItem: {
        task: "",
        id: "",
      },
    };
    this.handleInput = this.handleInput.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  handleInput(e) {
    this.setState({
      currentItem: {
        task: e.target.value,
        id: Date.now(),
      },
    });
  }

  addItem(e) {
    e.preventDefault();
    const newItem = this.state.currentItem;

    if (newItem.task !== "") {
      const newTodo = [...this.state.currentItem, newItem];
      this.setState = {
        todos: newTodo,
        currentItem: {
          task: "",
          id: "",
        },
      };
      <MainContent todos = {this.state.todos} />
    }
  }

  render() {
    return (
      <div className="card">
        <input
          type="text"
          name="name"
          placeholder="Add task"
          value={this.state.currentItem.task}
          className="buttonInp"
          onChange={this.handleInput}
        />

        <input
          type="submit"
          value="Add item"
          className="buttonAdd"
          onClick={this.addItem}
        />
      </div>
    );
  }
}

export default AddContent;
