import React from "react";
import "./index.css";
import Item from "./Item";

class AddContent extends React.Component {
  constructor() {
    super();
    this.state = {
      todos: [],
      currentItem: {
        task: "",
        id: "",
      },
    };
    this.handleInput = this.handleInput.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  handleInput(e) {
    this.setState({
      currentItem: {
        task: e.target.value,
        id: Date.now(),
      },
    });
  }

  addItem(e) {
    e.preventDefault();
    const newItem = this.state.currentItem;

    if (newItem.task !== "") {
      const newTodo = [...this.state.todos, newItem];
      console.log(newTodo);
      this.setState({
        todos: newTodo,
        currentItem: {
          task: "",
          id: "",
        },
      });
      console.log(this.state.Todos);
    }
  }

  handleDelete(key) {
    const items = this.state.todos.filter((item) => item.id !== key);
    this.setState({
      todos: items,
    });
    console.log("handle delete called key = ", key);
  }

  render() {
    const arr = this.state.todos.map((n) => (
      <Item key={n.id} num={n} handleDelete={this.handleDelete} />
    ));

    return (
      <div>
        <div className="card">
          <input
            type="text"
            name="name"
            placeholder="Add task"
            value={this.state.currentItem.task}
            className="buttonInp"
            onChange={this.handleInput}
          />

          <input
            type="submit"
            value="Add item"
            className="buttonAdd"
            onClick={this.addItem}
          />
        </div>
        <div className="card" style={{height:"400px"}}>{arr}</div>
      </div>
    );
  }
}

export default AddContent;
